import { LineChart } from "@d3/line-chart"

import { aapl } from "@d3/line-chart"

const chart = () => {
    return LineChart(aapl, {
        x: d => d.date,
        y: d => d.close,
        yLabel: "↑ Daily close ($)",
        height: 500,
        color: "steelblue"
    })   
}

export default chart
